/// Header files
#include <stdint.h>
#include <main.h>

/// function declarations
void pin_init();

#if !defined(__SOFT_FP__) && defined(__ARM_FP)
  #warning "FPU is not initialized, but the project is compiling for an FPU. Please initialize the FPU before use."
#endif

void pin_init()
{
	/// setting the clock lines for GPIO A and D
	RCC->AHB1_peripheral_clock_enable_reg |= SET_GPIOAEN_RCC;
	RCC->AHB1_peripheral_clock_enable_reg |= SET_GPIODEN_RCC;
	/// Clearing the mode reg for GPIOD 15th,14th pin for LED , 0th pin in GPIOA for SWITCH
	GPIOD->mode_reg &= CLEAR_MODER15_GPIOD;
	GPIOD->mode_reg &= CLEAR_MODER14_GPIOD;
	GPIOA->mode_reg &= CLEAR_MODER0_GPIOA;
	/// Setting the pin 15,14 in GPIOD as output , 0th pin in GPIOA as input
	GPIOD->mode_reg |= SET_MODER15_GPIOD;
	GPIOD->mode_reg |= SET_MODER14_GPIOD;
	GPIOA->mode_reg |= SET_MODER0_GPIOA;
}

int main(void)
{
	pin_init();
	while (1)
	{
		/// Turn ON the blue LED if switch is pressed , else red
		if(GPIOA->input_data_reg & 0x01)
		{
			GPIOD->output_data_reg |= SET_ODR15_GPIOD;
			GPIOD->output_data_reg &= CLEAR_ODR14_GPIOD;
		}
		else
		{
			GPIOD->output_data_reg |= CLEAR_ODR15_GPIOD;
			GPIOD->output_data_reg &= SET_ODR14_GPIOD;
		}
	}
}
